import React from 'react'
import PropTypes from 'prop-types'
import coinify from 'coinify'

import { currencyWording } from './utils'

/**
 * **Currency — a presentational component mapped to a specific currency & amount.**
 *
 * A <Currency /> instance is refered to as a "currency widget".
 */
class Currency extends React.Component {
  /**
   * User edited the widget's amount.
   *
   * Delegates to the matching callback provided as `props.onAmountEdited`.
   *
   * @see {@link App#onAmountEdited}
   * @see https://reactjs.org/docs/events.html
   *
   * @param {SyntheticEvent} event - "change" event
   */
  onAmountEdited(event) {
    this.props.onAmountEdited({
      uuid: this.props.uuid,
      amount: event.target.value
    })
  }

  /**
   * User edited the widget's currency.
   *
   * Delegates to the matching callback provided as `props.onCurrencyEdited`.
   *
   * @see {@link App#onCurrencyEdited}
   * @see https://reactjs.org/docs/events.html
   *
   * @param {SyntheticEvent} event - "change" event
   */
  onCurrencyEdited(event) {
    this.props.onCurrencyEdited({
      uuid: this.props.uuid,
      amount: this.props.amount,
      currency: event.target.value
    })
  }

  /**
   * Renders the currency widget.
   *
   * @return {ReactElement}
   */
  render() {
    const currencies = this.props.currencies.map((currency) =>
      <option
        key={currency}
        className='fxp-currency__list-item'
        value={currency}>
          {currencyWording(currency)}
      </option>
    )
    const currencySymbol = coinify.symbol(this.props.currency)
    return (
      <div className='fxp-currency'>
        <div className='fxp-currency__result'>
          <input
            type='text'
            className='fxp-currency__amount'
            onChange={this.onAmountEdited.bind(this)}
            value={this.props.amount || ''}
            // ref={input => input && this.props.uuid === 0 && input.focus()}
          />
          <span className='fxp-currency__label'>{currencySymbol}</span>
        </div>
        <select value={this.props.currency} className='fxp-currency__list' onChange={this.onCurrencyEdited.bind(this)}>
        {currencies}
        </select>
      </div>
    )
  }
}

Currency.propTypes = {
  /** @type {string} */
  currency: PropTypes.string.isRequired,
  /** @type {Array} */
  currencies: PropTypes.array.isRequired,
  /** @type {function} */
  onAmountEdited: PropTypes.func.isRequired,
  /** @type {function} */
  onCurrencyEdited: PropTypes.func.isRequired
}

export default Currency

import React, { Component } from 'react'

import { APP_TITLE, APP_MOTTO } from './utils'

/**
 * **StaticUI — A presentational wrapper to prettify the core app.**
 *
 * @type {function}
 * @param {React.Component} WrappedComponent - Expected to be {@link App}
 * @return {React.Component}
 */
export default (WrappedComponent) => {
  return class StaticUI extends Component {
    render() {
      return (
        <div className="fxp">
          <header>
            <h1 className="fxp-title">{APP_TITLE}</h1>
            <h2 className="fxp-motto">{APP_MOTTO}</h2>
          </header>
          <WrappedComponent {...this.props} />
          <footer>
            <ul className='fxp-u-list  fxp-footer-links'>
              <li className='fxp-u-list__item'>
                <a
                  className='fxp-footer-link'
                  href='https://github.com/chikamichi/fxpocket/'
                >source</a>
              </li>
              <li className='fxp-u-list__item'>
                <a
                  className='fxp-footer-link'
                  href='https://en.wikipedia.org/wiki/Exchange_rate'
                >about exchange rates</a>
              </li>
            </ul>
          </footer>
        </div>
      )
    }
  }
}

/**
 * **http://fixer.io/ API wrapper providing helper functions.**
 */
export default class Fixer {
  /**
   * `Fixer.connect()` - Synchronously pre-fetches available currencies and
   * matching conversion rates.
   *
   * One *must* use `connect()` to instanciate `Fixer`.
   *
   * @return {Fixer} a populated instance
  */
  static async connect() {
    const currencies = await this.fetchCurrencies()
    const rates = await this.fetchRates(currencies)
    return new Fixer(currencies, rates)
  }

  /** @ignore */
  constructor(currencies, rates) {
    if (typeof currencies === 'undefined') {
      throw new Error('Cannot be called directly, use Fixer.connect() instead.')
    }
    /** @type {Array<string>} */
    this.currencies = currencies
    /** @type {Object} */
    this.rates = rates
  }

  /**
   * `Fixer.fetchRates()` - Fetches conversion rates for specified currencies.
   *
   * Specified currencies must be available on the API.
   *
   * @param {Array<string>} currencies - available currencies
   * @return {Array<Object>} matching rates for each available currency
   */
  static fetchRates(currencies) {
    return Promise.all(currencies.map((currency) => {
      // Beware: as fetch().then() returns a Promise (for chainability), it must
      // be part of Promise.all().
      return fetch(`https://api.fixer.io/latest?base=${currency}`)
        .then(response => response.json())
    }))
    .then(data => {
      const extractRates = (acc, val) => {
        acc[val.base] = val.rates
        return acc
      }
      return data.reduce(extractRates, {})
    })
  }

  /**
   * `Fixer.fetchCurrencies()` - Fetches available currencies.
   *
   * @return {Array<string>} A list of ISO 4217 codes, alpha-sorted
   * @see https://en.wikipedia.org/wiki/ISO_4217
   */
  static fetchCurrencies() {
    return fetch(`https://api.fixer.io/latest?base=EUR`)
      .then(response => response.json())
      .then(data => {
        return [
          'EUR',
          ...Object.keys(data.rates),
        ].sort((a,b) => a.localeCompare(b))
      })
  }
}

import coinify from 'coinify'

import Fixer from './fixer'

/** @type {string} */
export const APP_TITLE = 'fxPocket'
/** @type {string} */
export const APP_MOTTO = 'Currency conversion made easy.'

/**
 * {@link App}'s initial state.
 *
 * First `widgets` entry is considered the "base" currency, next widgets are
 * "quote" currencies.
 *
 * @see https://www.investopedia.com/terms/c/currencypair.asp
 *
 * @type {Object}
 * @property {boolean} INITIAL_STATE.init - Keeps track of the app's async startup process
 * @property {Array<string>} INITIAL_STATE.widgets - A list of currency widgets to display at startup
 * @property {number|undefined} INITIAL_STATE.baseAmount - Keeps track of the base currency's amount
 */
export const INITIAL_STATE = {
  init: false,
  widgets: ['EUR', 'USD'],
  baseAmount: undefined
}

/**
 * Rounds floating numbers to n decimals.
 *
 * @see http://www.jacklmoore.com/notes/rounding-in-javascript/
 *
 * @param {number} value
 * @param {number} [decimals=2] - how many decimals to keep
 * @return {number}
 */
export function round(value, decimals = 2) {
  return Number(Math.round(value+'e'+decimals)+'e-'+decimals);
}

/**
 * Converts an amount from one currency to another.
 *
 * The function may be curried over a specific {@link Fixer} record.
 *
 * @param {Fixer} fixer - must provide conversion rates between specified currencies (any object abiding by Fixer's API is also valid)
 * @param {number} amount - the amount to convert
 * @param {string} baseCurrency - currency to convert from (amount's current unit)
 * @param {string} quoteCurrency - currency to convert to
 * @return {number} converted amount
 */
export function convertAmount(fixer, amount, baseCurrency, quoteCurrency) {
  if (arguments.length === 1 && arguments[0] instanceof Fixer)
    return (amount, baseCurrency, quoteCurrency) =>
      convertAmount.bind({}, fixer)(amount, baseCurrency, quoteCurrency)

  return amount * fixer.rates[baseCurrency][quoteCurrency]
}

/**
 * Batch-conversion of several amounts expressed in the same base currency, to
 * all supported quote currencies as specified by the Fixer record.
 *
 * The function may be curried over a specific {@link Fixer} record.
 *
 * @param {Fixer} fixer - must provide conversion rates between specified base currency and some quote currencies
 * @param {Object} context
 * @param {number} context.amount - the amount to convert
 * @param {string} context.currency - the base currency (amount's current unit)
 * @return {Object} a list of converted amounts for each supported quote currencies
 */
export function convertAmounts(fixer, context = {}) {
  if (arguments.length === 1 && arguments[0] instanceof Fixer)
    return (context = {}) => convertAmounts.bind({}, fixer)(context)

  if (!context.amount || !context.currency) return {}

  return context.currencies.reduce((acc, currency) => {
    let amount
    if (context.currency === currency)
      amount = context.amount
    else
      amount = convertAmount(fixer, context.amount, context.currency, currency)
    acc[currency] = round(amount)
    return acc
  }, {})
}

/**
 * User-friendly wording for a currency.
 *
 * Eg. for 'AUD', returns 'AUD / Australian Dollar'
 *
 * @param {string} currency
 * @return {string}
 */
export function currencyWording(currency) {
  const c = coinify.get(currency)
  return [
    c.code,
    '/',
    c.name
  ].join(' ')
}
